class MyAction extends Action {
    getFilenameFromUrl(url) {
        const filename = url.split('/').pop().split('#')[0].split('?')[0];
        if (filename.includes('.')) {
            return filename;
        }
        return undefined;
    }

    convertToRawUrl(url) {
        let convertedUrl = url;
        if (url.includes("github.com") && !url.includes("raw.githubusercontent.com") && url.includes("/blob/")) {
            convertedUrl = url.replace("github.com", "raw.githubusercontent.com").replace("/blob/", "/");
        }

        return this.encodeUrl(convertedUrl);
    }

    encodeUrl(url) {
        const [protocol, path] = url.split('://');
        const encodedPath = path.split('/').map(part => encodeURIComponent(part)).join('/');
        return `${protocol}://${encodedPath}`;
    }

    async downloadContent(url) {
        const response = await $http.get({
            url: this.convertToRawUrl(url), 
            showsProgress: true
        });
        if (response.error) {
            $ui.alert(response.error.localizedDescription);
        } else {
            return response;
        }
    }

    async do() {
        const urls = this.getUrls(); 
        let response = undefined;
        if (urls.length > 1) {
            $ui.menu({
                items: urls,
                handler: async (title, index) => {
                    response = await this.downloadContent(urls[index]);
                }
            });
        } else if (urls.length === 1) {
            response = await this.downloadContent(urls[0]);
        } else {
            $ui.warning("未检测到链接");
            return;
        }

        if (response) {
            let filename = this.getFilenameFromUrl(urls[0]) || response.response.suggestedFilename;
            $share.sheet([
                {
                    name: filename,
                    data: response.data
                }
            ]);
        }
        return response;
    }
}
